mapper.update:
insert into em_soc_devices_label ( id,device_id,fk_sys_device_label_id,status,create_time,last_update_time,version_id ) VALUES
#{item.id} , #{item.deviceId} , #{item.fkSysDeviceLabelId} , #{item.status} , #{item.createTime}, #{item.lastUpdateTime}, #{item.versionId}
ON DUPLICATE KEY UPDATE fk_sys_device_label_id=ifnull(values(fk_sys_device_label_id),fk_sys_device_label_id), status=ifnull(values(status),status), last_update_time=ifnull(values(last_update_time),last_update_time)

mapper.select:
select esdl.device_id,esd.external_addr,esd.internal_addr ,esdl.fk_sys_device_label_id ,sdl.label_name sysDeviceLabelName ,esdl.status ,esdl.create_time ,esdl.last_update_time from em_soc_devices_label esdl left join sys_device_label sdl on esdl.fk_sys_device_label_id = sdl.id left join em_soc_devices esd on esdl.device_id = esd.device_id
and esdl.device_id = #{param.deviceId}
and esd.external_addr = #{param.externalAddr}
and esdl.status = #{param.status}
