mapper.select:
select pd.* from pcp_device pd left join em_soc_devices esd
    on pd.device_id = esd.device_id

    pd.phone_id = #{cphPhoneId}
    and pd.serverId = #{cphServerId}
    and esd.status = 50
    and esd.health_status = 1

limit 0,1;

SELECT ${ew.sqlSelect}* FROM pcp_device


                    ${ew.sqlSegment}

                and id > #{lastId}

                ORDER BY id LIMIT #{pageSize}

SELECT ${ew.sqlSelect}* FROM pcp_device


                    ${ew.sqlSegment}


                and id > (
                    SELECT id FROM pcp_device


                                    ${ew.sqlSegment}


                                ORDER BY id LIMIT ${((pageNo-1)*pageSize)-1}, 1)


                ORDER BY id LIMIT #{pageSize}
