mapper.select:
SELECT ${ew.sqlSelect}* FROM viz_mc_default_update_device_task


                    ${ew.sqlSegment}

                and id > #{lastId}

                ORDER BY id LIMIT #{pageSize}

SELECT ${ew.sqlSelect}* FROM viz_mc_default_update_device_task


                    ${ew.sqlSegment}


                and id > (
                    SELECT id FROM viz_mc_default_update_device_task


                                    ${ew.sqlSegment}


                                ORDER BY id LIMIT ${((pageNo-1)*pageSize)-1}, 1)


                ORDER BY id LIMIT #{pageSize}

select count(mdudt.id) from viz_mc_default_update_device_task mdudt left join viz_mc_update_device_task mudt on mdudt.fk_mc_update_device_task_id = mudt.id left join viz_mc_service_software_package mssp on mudt.fk_mc_server_software_package_id = mssp.id left join viz_mc_service_software_package_type msspt on mssp.fk_mc_service_software_package_id = msspt.id
mdudt.status = 50
and msspt.id = #{fkMcServiceSoftwarePackageTypeId}
and mssp.supplier_type = #{supplierType}
