mapper.select:
SELECT ${ew.sqlSelect}* FROM viz_mc_update_device_task_tenant


                    ${ew.sqlSegment}

                and id > #{lastId}

                ORDER BY id LIMIT #{pageSize}

SELECT ${ew.sqlSelect}* FROM viz_mc_update_device_task_tenant


                    ${ew.sqlSegment}


                and id > (
                    SELECT id FROM viz_mc_update_device_task_tenant


                                    ${ew.sqlSegment}


                                ORDER BY id LIMIT ${((pageNo-1)*pageSize)-1}, 1)


                ORDER BY id LIMIT #{pageSize}

select mudtt.id, mudtt.fk_tenant_id, mudt.name mcUpdateDeviceTaskName, mssp.supplier_type, msspt.name serviceSoftwarePackageTypeName , mssp.name serviceSoftwarePackageName, mudtt.create_time, mudtt.last_update_time from viz_mc_update_device_task_tenant mudtt left join viz_mc_update_device_task mudt on mudtt.fk_mc_update_device_task_id = mudt.id left join viz_mc_service_software_package mssp on mudt.fk_mc_server_software_package_id = mssp.id left join viz_mc_service_software_package_type msspt on mssp.fk_mc_service_software_package_id = msspt.id
mudtt.status = 50
and mudtt.fk_tenant_id = #{param.fkTenantId}

select count(mudtt.id)
    from viz_mc_update_device_task_tenant mudtt
    left join viz_mc_update_device_task mudt on mudtt.fk_mc_update_device_task_id = mudt.id
    left join viz_mc_service_software_package mssp on mudt.fk_mc_server_software_package_id = mssp.id
    left join viz_mc_service_software_package_type msspt on mssp.fk_mc_service_software_package_id = msspt.id


        and mudtt.fk_tenant_id = #{fkTenantId}


        and msspt.id = #{fkMcServiceSoftwarePackageTypeId}


        and mssp.supplier_type = #{supplierType}
